package Controlstructures;
import java.util.Scanner;
public class pattern2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
    Scanner s=new Scanner(System.in);
    int i,j;
    for(i=0;i<4;i++)
    {
    	for(j=i;j<4;j++)
    	{
    		System.out.print("*");
    	}
    	System.out.print("\n");
    }
    
	}

}
